import collections
import functools
import heapq


class Solution:
    # TLE
    def minCost(self, houses, cost, m: int, n: int, target: int) -> int:
        queue = []
        if houses[0] == 0:
            for c in range(n):
                heapq.heappush(queue, (cost[0][c], target - 1, 1, c + 1, 1))
        else:
            heapq.heappush(queue, (0, target - 1, 1, houses[0], 1))

        while queue:
            current_cost, h, index, last_house_color_id, neighbourhoods = heapq.heappop(queue)
            if neighbourhoods > target:
                continue
            if index == m:
                if neighbourhoods == target:
                    return current_cost
            if index < m:
                if houses[index] != 0:
                    new_neighbourhoods = neighbourhoods + (1 if houses[index] != last_house_color_id else 0)
                    if new_neighbourhoods <= target:
                        heapq.heappush(queue, (current_cost, target - new_neighbourhoods, index + 1, houses[index], new_neighbourhoods))
                else:
                    for c in range(n):
                        new_neighbourhoods = neighbourhoods + (1 if c + 1 != last_house_color_id else 0)
                        if new_neighbourhoods <= target:
                            heapq.heappush(queue, (current_cost + cost[index][c], target - new_neighbourhoods, index + 1, c + 1, new_neighbourhoods))

        return -1

    # TLE
    def minCost(self, houses, cost, m: int, n: int, target: int) -> int:
        def dfs(index, current_cost, last_house_color_id, stack, neighbourhoods):
            if neighbourhoods > target:
                return float('inf')
            if index == len(houses):
                return current_cost if neighbourhoods == target else float('inf')
            if houses[index] != 0:
                new_neighbourhoods = neighbourhoods + (1 if houses[index] != last_house_color_id else 0)
                return dfs(index + 1, current_cost, houses[index], stack + [houses[index]], new_neighbourhoods)
            else:
                min_cost = float('inf')
                for c in range(n):
                    new_neighbourhoods = neighbourhoods + (1 if c + 1 != last_house_color_id else 0)
                    min_cost = min(min_cost, dfs(index + 1, current_cost + cost[index][c], c + 1, stack + [c + 1], new_neighbourhoods))

                return min_cost

        ans = dfs(0, 0, -2, [], 0)
        return ans if ans != float('inf') else -1

    """
    This was a bit tough one. My idea was to use BFS and DFS to solve the problem but the problem with those two is that, BFS reaches the solution first but it needs to traverse the tree thus formed row by row.
    And DFS needs to take all the possibilities into consideration before reaching the minimum value.
    The idea is to use dp and instead of solving the problem top down and searching for the minimum cost, build bottom up and use the previous value from dp.
    """
    def minCost(self, houses, cost, m: int, n: int, target: int) -> int:

        @functools.lru_cache(None)
        def cost_func(index, color, neighbourhood):
            if neighbourhood > target:
                return float('inf')
            if index == -1:
                return 0 if neighbourhood == target else float('inf')
            if houses[index] != 0:
                return cost_func(index - 1, houses[index], neighbourhood + (1 if houses[index] != color else 0))
            else:
                min_cost = float('inf')
                for c in range(n):
                    min_cost = min(min_cost, cost[index][c] + cost_func(index - 1, c + 1, neighbourhood + (1 if c + 1 != color else 0)))
                return min_cost

        ans = cost_func(m - 1, -2, 0)
        return ans if ans != float('inf') else -1













































    """
    Attempt: Fired
    Accepted: 60%
    """
    def minCost(self, houses, cost, m: int, n: int, target: int) -> int:

        @functools.lru_cache(None)
        def search(index, last_house_color, remaining_target_count):
            if index == m:
                return 0 if remaining_target_count == 0 else float('inf')
            if remaining_target_count < 0:
                return float('inf')
            if houses[index] != 0:
                return search(index + 1, houses[index], remaining_target_count if houses[index] == last_house_color else remaining_target_count - 1)

            min_cost = float('inf')
            for color in range(1, n + 1):
                min_cost = min(min_cost, cost[index][color - 1] + search(index + 1, color, remaining_target_count if color == last_house_color else remaining_target_count - 1))

            return min_cost

        ans = search(0, -1, target)
        return ans if ans != float('inf') else -1





